  {id: 1, title: "The Shawshank Redemption", year: 1994, duration: 142, description: "Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.", image_url: "http://ia.media-imdb.com/images/M/MV5BMTc3NjM4MTY3MV5BMl5BanBnXkFtZTcwODk4Mzg3OA@@._V1_SY317_CR4,0,214,317_.jpg", director_id: 1},
  {id: 2, title: "The Godfather", year: 1972, duration: 175, description: "The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjEyMjcyNDI4MF5BMl5BanBnXkFtZTcwMDA5Mzg3OA@@._V1_SX214_.jpg", director_id: 2},
  {id: 3, title: "The Godfather: Part II", year: 1974, duration: 200, description: "The early life and career of Vito Corleone in 1920s New York is portrayed while his son, Michael, expands and tightens his grip on his crime syndicate stretching from Lake Tahoe, Nevada to pre-revolution 1958 Cuba.", image_url: "http://ia.media-imdb.com/images/M/MV5BNDc2NTM3MzU1Nl5BMl5BanBnXkFtZTcwMTA5Mzg3OA@@._V1_SX214_.jpg", director_id: 2},
  {id: 4, title: "Pulp Fiction", year: 1994, duration: 154, description: "The lives of two mob hit men, a boxer, a gangster's wife, and a pair of diner bandits intertwine in four tales of violence and redemption.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjE0ODk2NjczOV5BMl5BanBnXkFtZTYwNDQ0NDg4._V1_SY317_CR4,0,214,317_.jpg", director_id: 3},
  {id: 5, title: "The Good, the Bad and the Ugly", year: 1966, duration: 161, description: "A bounty hunting scam joins two men in an uneasy alliance against a third in a race to find a fortune in gold buried in a remote cemetery.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjIxNjYwNDMzMl5BMl5BanBnXkFtZTcwODA5Mzg3OA@@._V1_SX214_.jpg", director_id: 4},
  {id: 6, title: "The Dark Knight", year: 2008, duration: 152, description: "When Batman, Gordon and Harvey Dent launch an assault on the mob, they let the clown out of the box, the Joker, bent on turning Gotham on itself and bringing any heroes down to his level.", image_url: "http://ia.media-imdb.com/images/M/MV5BMTMxNTMwODM0NF5BMl5BanBnXkFtZTcwODAyMTk2Mw@@._V1_SY317_CR0,0,214,317_.jpg", director_id: 5},
  {id: 7, title: "12 Angry Men", year: 1957, duration: 96, description: "A dissenting juror in a murder trial slowly manages to convince the others that the case is not as obviously clear as it seemed in court.", image_url: "http://ia.media-imdb.com/images/M/MV5BODQwOTc5MDM2N15BMl5BanBnXkFtZTcwODQxNTEzNA@@._V1_SX214_.jpg", director_id: 6},
  {id: 8, title: "Schindler's List", year: 1993, duration: 195, description: "In Poland during World War II, Oskar Schindler gradually becomes concerned for his Jewish workforce after witnessing their persecution by the Nazis.", image_url: "http://ia.media-imdb.com/images/M/MV5BMTIxOTAxNTc4NF5BMl5BanBnXkFtZTcwOTg1NzQyMQ@@._V1_SY317_CR3,0,214,317_.jpg", director_id: 7},
  {id: 9, title: "The Lord of the Rings: The Return of the King", year: 2003, duration: 201, description: "Gandalf and Aragorn lead the World of Men against Sauron's army to draw his gaze from Frodo and Sam as they approach Mount Doom with the One Ring.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjE4MjA1NTAyMV5BMl5BanBnXkFtZTcwNzM1NDQyMQ@@._V1_SX214_.jpg", director_id: 8},
  {id: 10, title: "Fight Club", year: 1999, duration: 139, description: "An insomniac office worker looking for a way to change his life crosses paths with a devil-may-care soap maker and they form an underground fight club that evolves into something much, much more...", image_url: "http://ia.media-imdb.com/images/M/MV5BMjIwNTYzMzE1M15BMl5BanBnXkFtZTcwOTE5Mzg3OA@@._V1_SX214_.jpg", director_id: 9},
  {id: 11, title: "Star Wars: Episode V - The Empire Strikes Back", year: 1980, duration: 124, description: "After the rebels have been brutally overpowered by the Empire on their newly established base, Luke Skywalker takes advanced Jedi training with Master Yoda, while his friends are pursued by Darth Vader as part of his plan to capture Luke.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjE2MzQwMTgxN15BMl5BanBnXkFtZTcwMDQzNjk2OQ@@._V1_SY317_CR0,0,214,317_.jpg", director_id: 10},
  {id: 12, title: "The Lord of the Rings: The Fellowship of the Ring", year: 2001, duration: 178, description: "A meek hobbit of The Shire and eight companions set out on a journey to Mount Doom to destroy the One Ring and the dark lord Sauron.", image_url: "http://ia.media-imdb.com/images/M/MV5BNTEyMjAwMDU1OV5BMl5BanBnXkFtZTcwNDQyNTkxMw@@._V1_SY317_CR1,0,214,317_.jpg", director_id: 8},
